mapper.select:
SELECT 

FROM task a

WHERE a.id = #{id}

SELECT 

FROM task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
